mapper.insert:
insert into EQP_MST_PDM (MODEL_NAME,RAWID,AREA_MST_RAWID,NAME,DATA_TYPE_CD,DESCRIPTION
, CREATE_BY,CREATE_DTTS,UPDATE_BY,UPDATE_DTTS) values (#{areaEqp_name,jdbcType=VARCHAR},#{eqp_id,jdbcType=NUMERIC},#{area_id,jdbcType=NUMERIC}, #{name,jdbcType=VARCHAR}, #{data_type,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}
,'SYSTEM', SYSTIMESTAMP, 'SYSTEM', SYSTIMESTAMP)

mapper.select:
select *
from overall_spec_pdm
where param_id = #{param_id}

select
    node_id as eqp_id,
    alarm_dt as alarm_dtts,
    alarm_cd as status_cd
from HKMC_UV_ALARM_STATUS
where alarm_dt >= #{start}
  and alarm_dt < #{end}
  and node_id = #{eqpId}

mapper.delete:
delete from eqp_alarm_trx_pdm
where alarm_dtts >= #{start}
and alarm_dtts < #{end}
and eqp_id = #{eqpId}

delete from alarm_trx_pdm
where alarm_dtts >= #{start}
  and alarm_dtts < #{end}
  and param_id in (select param_id from param_pdm where eqp_id = #{eqpId})

delete from trace_raw_trx_pdm
where measure_dtts >= #{start}
  and measure_dtts < #{end}
  and param_id in (select param_id from param_pdm where eqp_id = #{eqpId})

mapper.update:
update overall_minute_trx_pdm
set alarm=#{alarm},
warn=#{warn}
where
param_id=#{param_id}
and read_dtts >= #{start}
and read_dtts < #{end}

mapper.delete:
delete from measure_trx_pdm
where measure_dtts >= #{start}
and measure_dtts < #{end}
and param_id = #{param_id}

delete from measure_trx_bin_pdm
where measure_trx_id in (
select
a.measure_trx_id
from measure_trx_bin_pdm a
join measure_trx_pdm b
on b.measure_trx_id = a.measure_trx_id
where
b.measure_dtts >= #{start}
and b.measure_dtts < #{end}
and b.param_id = #{param_id}
)

delete from overall_minute_trx_pdm
where read_dtts >= #{start}
  and read_dtts < #{end}

delete from overall_minute_trx_pdm
where param_id = #{paramId}
  and read_dtts >= #{start}
  and read_dtts < #{end}
