mapper.select:
select rawid as bearing_id,model_number,manufacture,bpfo,bpfi,bsf,ftf,description
from bearing_mst_pdm
where 1=1

  and model_number = #{modelNumber}


  and manufacture = #{manufacture}

mapper.delete:
delete from bearing_mst_pdm
where model_number = #{modelNumber}
  and manufacture = #{manufacture}

mapper.update:
update bearing_mst_pdm
set bpfo = #{bpfo,jdbcType=NUMERIC},
    bpfi = #{bpfi,jdbcType=NUMERIC},
    bsf = #{bsf,jdbcType=NUMERIC},
    ftf = #{ftf,jdbcType=NUMERIC},
    description = #{description,jdbcType=VARCHAR}
where model_number = #{model_number}
  and manufacture = #{manufacture}

mapper.select:
select *
from Bearing_pdm
where manufacture = #{manufacture}
and model_number = #{modelNumber}
