mapper.select:
select a.rawid as eqp_id, a.name, a.description, a.image as binary, b.name as data_type, a.offline_yn, a.model_name from eqp_mst_pdm a left outer join code_mst_pdm b on a.data_type_cd = b.code
where area_mst_rawid = #{areaId}
order by a.name

mapper.update:
update eqp_mst_pdm
set name = #{name},
    description = #{description,jdbcType=VARCHAR},
    area_mst_rawid = #{area_id},
    offline_yn = #{offline_yn},
    update_dtts = systimestamp,
    update_by = #{userName},
    model_name= #{model_name}

        ,image = #{binary,jdbcType=VARCHAR}

where rawid = #{eqp_id}

update eqp_mst_pdm
set name = #{name},
    description = #{description,jdbcType=VARCHAR},
    area_mst_rawid = #{area_id},
    offline_yn = #{offline_yn},
    update_dtts = localtimestamp,
    update_by = #{userName}

        ,image = #{binary,jdbcType=VARCHAR}

where rawid = #{eqp_id}

update eqp_etc_pdm
set image = #{binary,jdbcType=VARCHAR}
where eqp_id = #{eqp_id}
