mapper.select:
select a.rawid as param_id, e.rawid as eqp_id, e.name as eqp_name, a.name, a.description, (select name from code_mst_pdm where code=a.param_type_cd and category='param_type' and used_yn='Y') as param_type_name, (select name from code_mst_pdm where code=a.unit_cd and category='unit_type' and used_yn='Y') as unit_name, a.param_type_cd, a.unit_cd, a.parts_mst_rawid as parts_id, parts.name as parts_name
from param_mst_pdm a join eqp_mst_pdm e on a.eqp_mst_rawid = e.rawid
left join parts_mst_pdm parts on parts.rawid = a.parts_mst_rawid where a.rawid = #{paramId}

mapper.update:
update param_common_pdm
set eu = #{eu,jdbcType=VARCHAR},
    eu_type = #{eu_type,jdbcType=NUMERIC}
where param_mst_rawid = #{param_id}

mapper.select:
select rawid,param_mst_rawid as param_id,alarm_spec alarm , warning_spec warn
from trace_spec_mst_pdm
where param_mst_rawid = #{param_id}

select distinct name from param_mst_pdm where eqp_mst_rawid in
#{item.value}
order by name

select a.rawid as param_id, e.rawid as eqp_id, e.name as eqp_name, a.name, a.description, (select name from code_mst_pdm where code=a.param_type_cd and category='param_type' and used_yn='Y') as param_type_name, (select name from code_mst_pdm where code=a.unit_cd and category='unit_type' and used_yn='Y') as unit_name, a.param_type_cd, a.unit_cd, a.parts_mst_rawid as parts_id, parts.name as parts_name
from param_mst_pdm a join eqp_mst_pdm e on a.eqp_mst_rawid = e.rawid
left join parts_mst_pdm parts on parts.rawid = a.parts_mst_rawid where a.rawid = #{param_id}
