mapper.select:
select a.rawid as part_id, a.name, b.rawid as eqp_id, b.name as eqp_name, a.ratio, a.rpm, c.model_number, c.manufacture, a.bearing_mst_rawid as bearing_id, a.base_ratio_yn, a.parts_type_cd, ( select name from code_mst_pdm where code = a.parts_type_cd and category='parts_type' and used_yn='Y' ) as parts_type from parts_mst_pdm a join eqp_mst_pdm b on a.eqp_mst_rawid = b.rawid left outer join bearing_mst_pdm c on a.bearing_mst_rawid = c.rawid
where a.eqp_mst_rawid = #{eqpId}
order by a.name

select a.rawid as part_id, a.name, b.rawid as eqp_id, c.rawid as param_id, a.ratio,
a.base_ratio_yn, a.parts_type_cd, (select name from code_mst_pdm where code = a.parts_type_cd and category='part_type' and used_yn='Y') as parts_type
from parts_mst_pdm a join eqp_mst_pdm b on a.eqp_mst_rawid = b.rawid left outer join param_mst_pdm c on a.rawid = c.parts_mst_rawid where a.rawid=#{partId}
