mapper.select:
select a.eqp_id, a.occur_dtts as eventdate, a.cause1 as failuretype, a.cause2 as failurecomments1, a.cause3 as failurecomments2 from report_alarm_trx_pdm a where 1=1
and a.eqp_id = #{eqp_id}
and a.occur_dtts >= #{fromdate} and a.occur_dtts < #{todate}

mapper.update:
update report_alarm_trx_pdm
write_dtts=#{write_dtts}, state_cd=#{state_cd}, request_dtts=#{request_dtts}, request_user_id=#{request_user_id}, complete_dtts=#{complete_dtts}, complete_user_id=#{complete_user_id}, cause_update=#{cause_update}, content=#{content},
where eqp_id = #{eqp_id} and occur_dtts = #{occur_dtts}

mapper.delete:
delete from report_alarm_trx_pdm
where eqp_id = #{eqp_id}
  and occur_dtts = #{occur_dtts}

delete from batch_job_hst_pdm
where job_dtts = #{job_dtts}
and job_cd = #{job_cd}

mapper.select:
select a.*,
case when a.eqp_id is null then ''
else (select name from eqp_pdm where eqp_id = a.eqp_id) end as eqp_name
from batch_job_hst_pdm a
where a.job_dtts >= #{start}
and a.job_dtts < #{end}

    and a.job_type_cd = #{job_type_cd}

order by a.job_dtts desc

mapper.insert:
insert into overall_minute_avg90_trx_pdm (param_id, read_dtts, value)
select
param_id,
#{date},
avg(value)
from trace_trx_pdm
where read_dtts >= #{before90}
and read_dtts < #{date}

    AND param_id in (select param_id from param_pdm where eqp_id in

        #{eqpId}

    )

group by param_id

mapper.delete:
delete from overall_minute_avg90_trx_pdm
where read_dtts = #{date}

    AND param_id in (select param_id from param_pdm where eqp_id in

        #{eqpId}

    )

mapper.select:
select
a.param_id,
nvl(b.value, 0) - a.today_value as variation
from (
select a.param_id, avg(a.value) as today_value
from trace_trx_pdm a
join param_pdm b
on a.param_id = b.param_id
where b.eqp_id = #{eqp_id}
and a.read_dtts >= #{start}
and a.read_dtts < #{end}
group by a.param_id
) a left join overall_minute_avg90_trx_pdm b
on a.param_id = b.param_id
where b.read_dtts = #{start} - 1

select
    *
from param_feature_trx_pdm
where
param_mst_rawid = #{paramId}
and end_dtts >= #{fromDate}
and end_dtts < #{toDate}
order by end_dtts

SELECT
AVG(value) avg
FROM
trace_trx_pdm a,
( SELECT
min * ( avg - min ) / 50 AS lowbound,
min,
avg
FROM
( SELECT
MIN(value) AS min,
AVG(value) AS avg
FROM
trace_trx_pdm
WHERE param_mst_rawid = #{param_id}
and event_dtts >= #{start}
and event_dtts < #{end}
)
) sts
where
a.param_mst_rawid =#{param_id}
and event_dtts >= #{start}
and event_dtts < #{end}
AND a.value > sts.lowbound

select a.rawid as eqp_id, a.area_mst_rawid as area_id, a.name, min(b.sum_dtts) as start_dtts, max(b.status_cd) as status, max(b.value) as value from eqp_mst_pdm a join eqp_alarm_daily_sum_pdm b on a.rawid = b.eqp_mst_rawid where b.sum_dtts >= #{start} and b.sum_dtts < #{end}
and a.rawid = #{eqpId}
and b.status_cd in (128, 256) group by a.area_mst_rawid,a.rawid, a.name order by status desc, value desc

select
a.param_mst_rawid as param_id,
b.name,
a.cause
from trace_daily_sum_pdm a
join param_mst_pdm b on a.param_mst_rawid = b.rawid
where b.eqp_mst_rawid = #{eqpId}
and a.sum_dtts >= #{start}
and a.sum_dtts < #{end}

mapper.delete:
delete from trace_daily_sum_pdm
where param_mst_rawid = #{param_id}
and sum_dtts = #{read_dtts}

delete from trace_daily_sum_pdm
where param_mst_rawid in (select rawid from param_mst_pdm where eqp_mst_rawid =#{eqpId})
and sum_dtts  >= #{start}
and sum_dtts  < #{end}
