) worsteqp,
eqp_mst_pdm eqp where 1=1 and worsteqp.eqp_id=eqp.rawid and eqp.area_mst_rawid= #{area_id}

select worsteqp.eqp_id, worsteqp.status, worsteqp.start_dtts, worsteqp.end_dtts from ( select eqpstatus.eqp_id, eqpstatus.eqp_name,score.score, eqpstatus.status, eqpstatus.start_dtts, eqpstatus.end_dtts from ( select eqp_id,eqp_name, case when event_type_cd is null then case when type_cd = 'E' then 'RUN' else 'IDLE' end else case when event_type_cd = 'S' then 'RUN' else 'IDLE' end end status, start_dtts, end_dtts from ( select eqp_id,eqp_name, event_type_cd, lead(event_type_cd) over (partition by eqp_name order by event_dtts) type_cd, event_dtts as start_dtts, COALESCE(lead(event_dtts) over (partition by eqp_name order by event_dtts), TO_TIMESTAMP(#{end_dtts}, 'YYYY-MM-DD HH24:MI:SS')) end_dtts from ( select e.rawid as eqp_id, e.name as eqp_name, v.event_name, v.event_type_cd, t.event_dtts from eqp_mst_pdm e, eqp_event_mst_pdm v, eqp_event_trx_pdm t where e.rawid=v.eqp_mst_rawid and v.rawid=t.eqp_event_mst_rawid and v.process_yn='Y' and t.event_dtts between TO_TIMESTAMP(#{start_dtts}, 'YYYY-MM-DD HH24:MI:SS') and TO_TIMESTAMP(#{end_dtts}, 'YYYY-MM-DD HH24:MI:SS') union all select rawid as eqp_id, name as eqp_name, 'start', '' as typ, event_dtts from eqp_mst_pdm a, (select TO_TIMESTAMP(#{start_dtts}, 'YYYY-MM-DD HH24:MI:SS') as event_dtts) b order by eqp_name, event_dtts ) zz ) zzz
) eqpstatus,
( select e.rawid as eqp_id,name as eqp_name, max(h.score) as score from eqp_mst_pdm e, eqp_health_trx_pdm h where e.rawid=h.eqp_mst_rawid and e.offline_yn='N' and h.ALARM_DTTS between TO_TIMESTAMP(#{start_dtts}, 'YYYY-MM-DD HH24:MI:SS') and TO_TIMESTAMP(#{end_dtts}, 'YYYY-MM-DD HH24:MI:SS') group by e.RAWID,name order by score desc ) score
where 1=1 and eqpstatus.eqp_id=score.eqp_id
) worsteqp, eqp_mst_pdm eqp where 1=1 and worsteqp.eqp_id=eqp.rawid and eqp.area_mst_rawid= #{area_id}

select area.rawid as area_id, area.name as area_name, eqp.rawid as eqp_id, eqp.name as eqp_name , eqphealth.health_logic_mst_rawid, eqphealth.score, eqphealth.alarm_count
from eqp_health_daily_sum_pdm eqphealth, eqp_mst_pdm eqp, area_mst_pdm area
where 1=1
and area.rawid=eqp.area_mst_rawid
and eqphealth.EQP_MST_RAWID=eqp.rawid
and eqphealth.sum_dtts >= #{fromdate}
and eqphealth.sum_dtts < #{todate}
and area.rawid= #{area_id}

select a.rawid as param_health_id, a.param_mst_rawid as param_id, a.health_logic_mst_rawid as health_logic_id, a.APPLY_LOGIC_YN, b.rawid as health_logic_option_id, b.OPTION_NAME, b.option_value
from param_health_mst_pdm a, health_logic_option_mst_pdm b
where 1=1
and a.HEALTH_LOGIC_MST_RAWID=b.HEALTH_LOGIC_MST_RAWID
and a.param_mst_rawid= #{param_id}
and a.health_logic_mst_rawid= #{health_logic_id}
