mapper.select:
select rawid as measure_trx_id, param_mst_rawid as param_id, data_type_cd, max_freq as end_freq, freq_count as spectra_line, rpm, sampling_time, event_dtts as measure_dtts, reserved_col1, reserved_col2, reserved_col3, reserved_col4, reserved_col5, binary_data as binary from trace_raw_trx_pdm where data_type_cd = #{data_type} and event_dtts >= #{start} and event_dtts < #{end}
and param_mst_rawid = #{param_id}
order by event_dtts










select
    max(rawid) as measure_trx_id,
    max(a.event_dtts) as measure_dtts
from trace_trx_pdm a,
(
select
distinct trace_trx_rawid
from trace_raw_trx_pdm
where
event_dtts >= #{start}
and event_dtts < #{end}
and param_mst_rawid = #{param_id}
and trace_trx_rawid is not null
) b
where a.rawid = b.trace_trx_rawid
order by event_dtts

select rpm
from trace_trx_pdm
where param_mst_rawid = #{param_id}
and event_dtts <= #{measure_dtts}
and rownum = 1
order by event_dtts desc

SELECT *
FROM trace_trx_pdm ,
(SELECT MIN(measure_dtts) specout_date
FROM trace_trx_pdm meas,
(SELECT trx.PARAM_ID,trx.read_dtts
FROM overall_minute_trx_pdm trx
WHERE trx.param_id =#{param_id}
AND trx.read_dtts >= #{start}
AND trx.read_dtts < #{end}
AND trx.value      > trx.alarm
) specout
WHERE meas.PARAM_ID    = specout.PARAM_ID
AND meas.MEASURE_DTTS >= specout.read_dtts
AND meas.MEASURE_DTTS  < specout.read_dtts+1
GROUP BY read_dtts
) specout_date_data
WHERE trace_trx_pdm.PARAM_ID   = #{param_id}
AND trace_trx_pdm.MEASURE_DTTS = specout_date_data.specout_date
ORDER BY measure_dtts














































































SELECT RAWID,PARAM_MST_RAWID,TRACE_TRX_RAWID,DATA_TYPE_CD, MAX_FREQ, FREQ_COUNT, RPM, SAMPLING_TIME, EVENT_DTTS, RESERVED_COL1, RESERVED_COL2, RESERVED_COL3, RESERVED_COL4, RESERVED_COL5
FROM trace_raw_trx_pdm,
(
    select *
    from
    (
        SELECT meas.event_dtts specout_date
        FROM trace_raw_trx_pdm meas,
            (
                SELECT trx.param_mst_rawid, trx.event_dtts
                FROM trace_trx_pdm trx

                WHERE trx.param_mst_rawid =#{param_id}

                AND trx.event_dtts >= #{start}
                AND trx.event_dtts < #{end}
                AND trx.value      > trx.warning_spec - abs(trx.warning_spec ) *  (  1- #{rate} )
            )
            specout
        WHERE meas.param_mst_rawid = specout.param_mst_rawid
        AND meas.event_dtts >=  specout.event_dtts
        AND meas.event_dtts < #{end}
    )
    where rownum < 2
) specout_date_data
WHERE trace_raw_trx_pdm.param_mst_rawid = #{param_id}
AND   trace_raw_trx_pdm.event_dtts = specout_date_data.specout_date
ORDER BY event_dtts

select *
from trace_trx_pdm m,
OVERALL_SPEC_PDM s
where m.param_id=#{param_id}
and m.param_id=s.param_id
and measure_dtts >= #{start}
and measure_dtts < #{end}
order by measure_dtts

mapper.delete:
delete
from trace_raw_trx_pdm
where trace_trx_rawid in
(
select rawid
from trace_trx_pdm
where 1=1
and param_mst_rawid=#{param_id}
and MEASURE_DTTS>= #{start}
and MEASURE_DTTS< #{end}
)

delete
from trace_raw_trx_pdm
where
param_mst_rawid=#{param_id}
and event_DTTS>= #{start}
and event_DTTS< #{end}

delete
from trace_trx_pdm
where 1=1
and param_id=#{param_id}
and MEASURE_DTTS>= #{start}
and MEASURE_DTTS< #{end}

mapper.update:
update trace_raw_trx_pdm
set binary_data=#{binary}
where trace_trx_rawid=0
